package com.mks.cache.xmc;

public class XmcMemcachedException extends RuntimeException {

	private static final long serialVersionUID = 8308020181967622094L;

	public XmcMemcachedException() {
		super();
	}

	public XmcMemcachedException(String message, Throwable cause) {
		super(message, cause);
	}

	public XmcMemcachedException(String message) {
		super(message);
	}

	public XmcMemcachedException(Throwable cause) {
		super(cause);
	}
}
